const NodeModal = (props) => {
  // ...existing code...
  const { node /* assumes modal receives node with { id, path, value } */, onClose } = props;

  const [editing, setEditing] = useState(false);
  const [editValueRaw, setEditValueRaw] = useState<string>("");

  useEffect(() => {
    // initialize editor when modal opens or node changes
    if (node) {
      // pretty-print for objects/arrays, otherwise string form
      setEditValueRaw(typeof node.value === "string" ? node.value : JSON.stringify(node.value, null, 2));
    } else {
      setEditValueRaw("");
    }
    setEditing(false);
  }, [node]);

  const startEdit = () => setEditing(true);

  const cancelEdit = () => {
    // revert to original and exit edit mode
    if (node) {
      setEditValueRaw(typeof node.value === "string" ? node.value : JSON.stringify(node.value, null, 2));
    }
    setEditing(false);
  };

  const parseInputValue = (raw: string) => {
    // try to parse JSON, fallback to raw string
    try {
      return JSON.parse(raw);
    } catch {
      return raw;
    }
  };

  const saveEdit = () => {
    if (!node) return;
    const newValue = parseInputValue(editValueRaw);

    // notify JSON editor to update left JSON model (listen for 'json-edit')
    window.dispatchEvent(
      new CustomEvent("json-edit", {
        detail: { path: node.path, value: newValue },
      })
    );

    // notify graph/tree visualizers they should update node state if they track node data separately
    window.dispatchEvent(
      new CustomEvent("node-update", {
        detail: { id: node.id, path: node.path, value: newValue },
      })
    );

    setEditing(false);
    // optionally close modal after save:
    onClose?.();
  };

  // ...existing JSX above/modal wrapper...
  return (
    // ...existing modal wrapper start ...
    <div className="node-modal-content">
      {/* existing content */}
      {/* ...existing code... */}

      <div className="node-info">
        <div className="node-path"><strong>Path:</strong> {node?.path}</div>

        <div className="node-value">
          <strong>Value:</strong>
          {!editing ? (
            <pre style={{ whiteSpace: "pre-wrap", marginTop: 8 }}>
              {typeof node?.value === "string" ? node?.value : JSON.stringify(node?.value, null, 2)}
            </pre>
          ) : (
            <textarea
              value={editValueRaw}
              onChange={(e) => setEditValueRaw(e.target.value)}
              rows={6}
              style={{ width: "100%", fontFamily: "monospace", marginTop: 8 }}
            />
          )}
        </div>
      </div>

      <div className="node-modal-actions" style={{ marginTop: 12, display: "flex", gap: 8 }}>
        {!editing ? (
          <button onClick={startEdit} aria-label="Edit value">Edit</button>
